$(function(){
  var img = $('#id-img_1');
  img.attr('initfilter',100);
  img.attr('initfiltro',100);
  img.attr('initfilt',0);
  $.IGRP.targets.void.action = function(p){
    var obj = $(p.clicked),
        action = obj.attr('position'),
        filter = img.attr('initfilter')*1,
        filt = img.attr('initfilt')*1,
        filtro = img.attr('initfiltro')*1;
    console.log('ok');
    if(action == 'diminuir_bril'){
      filter = filter - 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
    if(action == 'aumentar_bril'){
      filter = filter + 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
    if(action == 'diminuir_cont'){
      filtro = filtro - 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
    if(action == 'aumentar_cont'){
      filtro = filtro + 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
    if(action == 'aumentar_inv'){
      filt = filt + 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
    if(action == 'diminuir_inv'){
      filt = filt - 10;
      img.attr({'initfilter':filter, 'initfiltro':filtro, 'initfilt':filt}).css({'filter' : 'brightness('+filter+'%) contrast('+filtro+'%) invert('+filt+'%)'});
    }
  }
});
