css:
.gen-fa-icon {
   display: inline-block;
   padding: 5px;
   margin: 2px;
   font-size: 16px;
   cursor: pointer;
   color: #5d5d5d;
}

.fa {
   display: inline-block;
   font: normal normal normal 14px/1 FontAwesome;
   font-size: inherit;
   text-rendering: auto;
   vertical-align: middle;
   -webkit-font-smoothing: antialiased;
   -moz-osx-font-smoothing: grayscale;
}

a:focus, a:hover {
   color: #23527c;
   text-decoration: underline;
}

.gen-fa-icon.active {
   box-shadow: 0px 4px 7px #7b7b7b;
   outline: 1px solid #ececec;
   color: black;
   background: white;
}

ul li a {
   padding: 10px 5px !important;
   font-size: 12px;
   text-transform: uppercase;
}

.tab-content {
   max-height: 360px;
   overflow-y: auto;
   background: whitesmoke;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
   background-color: none !important;   
}

.nav .open>a, .nav .open>a:focus, .nav .open>a:hover {
   background-color: none !important; 
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
   background-color: none !important;   
}

.nav .open>a, .nav .open>a:focus, .nav .open>a:hover {
   background-color: none !important; 
}

.pull-right {
   float: right!important;
}

js:
$(function () {

   $.ajax({

      type: "GET",
      dataType: "json",
      url: path + '/core/fontawesome/fa.icons.json',
      success: function (fa) {

         var holder = $('<div class="gen-fa-setter" />'),
            ul = $('<ul class="nav nav-tabs icons-list-tab" />'),
            contents = $('<div class="tab-content icons-list-tab" />'),
            searcher = $('<div class="form-group icon-searcher col-md-12"><input class="form-control" type="text" placeholder="Pesquisar" /></div>'),
            searchRes = $('<div class="icon-search-fa clearfix" />'),
            idx = 0,
            dropdownMenu = $('<li class="dropdown pull-right tabdrop"><a class="dropdown-toggle" data-toggle="dropdown" href="#"> <i class="icon-align-justify"></i><b class="caret"></b></a></li>'),
            ulDropdownMenu = $('<ul class="dropdown-menu" />');

         holder.append([searcher, ul, contents, searchRes]);

         searcher.on('keyup', 'input', function () {

            var word = $(this).val(),
               parent = $(this).parents('.gen-fa-setter');

            if (word.length > 0) {

               $('.icons-list-tab', parent).hide();
               $('.gen-fa-icon', parent).hide();
               $('.icon-search-fa', parent).html('');

               var items = $('.gen-fa-icon[title*="' + word + '"]:not(.duplicated)', parent).clone().show();

               $('.icon-search-fa', parent).append(items);
               $('.icon-search-fa', parent).show();

            } else {

               $('.icons-list-tab', parent).show();
               $('.icons-list-tab .gen-fa-icon', parent).show();
               $('.icon-search-fa', parent).html('');
            }
         });         

         for (var c in fa) {

            var icons = fa[c],
               id = 'gen-fa-' + c,
               active = idx == 0 ? 'active in' : '',
               li = $('<li rel="' + c + '" class="' + active + '"><a data-toggle="tab" href="#'+id+'" >' + c + '</a></li>'),
               content = $('<div id="' + id + '" class="tab-pane fade ' + active + '"></div>');

            for (var i in icons) {

               var icon = $('<span class="gen-fa-icon " parent="' + c + '" rel="' + icons[i].value + '" title="'
                  + icons[i].label + '"><i class="fa ' + icons[i].value + '"></i></span>');

               content.append(icon);
            }

            contents.append(content);

            if (idx > 3) {
               ulDropdownMenu.append(li);
            } else {
               ul.append(li);
            }

            idx++;
         }

         dropdownMenu.append(ulDropdownMenu);
         ul.append(dropdownMenu);

         $('.content').append(holder[0]);

         var searchIcon = $('#p_fwl_search');

         if (searchIcon[0]) {
            searchIcon = searchIcon.val();

            if (searchIcon) {
               var chooseParent = $('span.gen-fa-icon[rel="' + searchIcon + '"]:last');

               $('ul li[rel="' + chooseParent.attr('parent') + '"] a').click();

               chooseParent.addClass('active');
            }
         }

      }
   });

   $('body').on('click', '.gen-fa-icon', function () {

      var _window, _close, iconClass, form;

      _window = window.opener ? window.opener ? _close = true : _close = false : window.parent;

      iconClass = $(this).attr('rel');

      form = _window.document.forms['formular_default'];

      var lookupName = $("*[name='jsonLookup']"),
         fieldName;

      lookupName = lookupName.val();
      lookupName = decodeURIComponent(lookupName);
      lookupName = $.parseJSON(lookupName);

      $.each(lookupName, function (key) {
         fieldName = key;
      });

      $("*[name='" + fieldName + "']", form).val(iconClass);

      _close ? window.close() : _window.$.IGRP.components.iframeNav.hide();

   });


});
